pragma solidity ^0.4.24;

import "./Tickets.sol";
import "./LotteryFactory.sol";
import "./ERC20.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";

/** @title Lottery contract */
contract Lottery {

    using SafeMath for uint;

    /** @dev Emits when new draw was created */
    event DrawCreated(uint256 _drawId);

    /** @dev Emits when draw was finished */
    event DrawFinished(uint256 _drawId);

    /// TEST EVENT. SHOULD BE REMOVED
    event CombinationSet(uint256 _drawId);

    /** @dev Contains different data for draw such as amount of tickets and winners, win combination, array of checked tickets */
    struct Draw {
        uint ticketCount;
        uint winnerCount;
        uint8[] winCombination; // Wining combination for the draw
        uint multiplier;
    }

    /** @dev Contains all draws */
    mapping(uint256 => Draw) public draws;

    /** @dev last checked draw(for multiplier) */
    uint256[] public lastCheckedDrawById;

    /** @dev last checked jackpot */
    uint256[] public checkedJackpot;

    /** @dev Draws amount */
    uint256 public drawsCount = 0;

    /** @dev Minimal amount of BET tokens to create draw */
    uint public minDeposit;

    /** @dev Stores index of current draw */
    uint256 public currentDraw = 1;

    /** @dev after this block lottery get start */
    uint256 public startBlock;

    /** @dev after this block new tickets will not accepted */
    uint256 public sellBlock;

    /** @dev before this block winner's ticket must be chosen */
    uint256 public stopBlock;

    /** @dev all players must get their reward before this block */
    uint256 public closeBlock;

    /** @dev Block number used for random */
    uint256 blockForRandom;

    /** @dev Stores amount of the jackpot */
    uint256 public jackpot;

    /** @dev Stores amount of the lottery bank */
    uint256 public bank;

    /** @dev Ticket price */
    uint256 public ticketPrice = 2 ether;

    /** @dev Address of the owner */
    address public owner;

    /** @dev Multipliers for every combination */
    uint256[8] multipliers;

    /** @dev Array of jackpot winners */
    address[] public jackpotWinners;

    /** @dev Tickets contract */
    Tickets public tickets;

    /** @dev BET token contract */
    ERC20 public token;

    constructor (address _owner, address _tickets, address _token, uint _minDeposit) public {
        owner = _owner;
        tickets = Tickets(_tickets);
        token = ERC20(_token);
        minDeposit = _minDeposit;

        multipliers[0] = 1000000 ether; // 1/11,688,053.52
        multipliers[1] = 50000 ether; // 1/913,129.18
        multipliers[2] = 100 ether; // 1/36,525.17
        multipliers[3] = 100 ether; // 1/14,494.11
        multipliers[4] = 7 ether; // 1/579.76
        multipliers[5] = 7 ether; // 1/701.33
        multipliers[6] = 4 ether; // 1/91.98
        multipliers[7] = 4 ether; // 1/38.32
    }

    /** @dev Provides only-owner functionality */
    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    /** 
     * Distribution of earned tokens for draw
     * @param _value BET amount
     */
    function serviceReward(uint256 _value) internal {
        if(_value < 1 ether)
            return;
        uint profit = _value.sub(jackpot);
        uint256 valueForBank = profit.mul(420).div(1000);
        uint256 valueForJackpot = profit.mul(420).div(1000);
        uint256 valueForOwner = profit.mul(160).div(1000);
        bank = valueForBank;
        jackpot += valueForJackpot;
        token.transfer(owner, valueForOwner);
    }

    /**
     * @dev Sets new price of the ticket
     * @param _price New price of the ticket
     */
    function setTicketPrice(uint256 _price) onlyOwner external {
        require(_price != 0);
        ticketPrice = _price;
    }

    /**
     * @dev Creates new draw. Can be called only by owner
     * @param _sellOverBlock Block after that new tickets will not accepted
     * @param _stopLotteryBlock Before this block winner's ticket must be chosen
     */
    function createDraw(uint256 _sellOverBlock, uint256 _stopLotteryBlock) external onlyOwner {
        require(currentDraw == drawsCount + 1, "Draw must be finished");
        require(token.balanceOf(this) >= minDeposit, "Insuficient amount of BET tokens");
        require(_sellOverBlock > block.number && _stopLotteryBlock > _sellOverBlock, "Wrong blocks");

        blockForRandom = _sellOverBlock.add(2); // FOR TESTING - CHANGE TO 246 IN RELEASE
        stopBlock = _stopLotteryBlock;
        sellBlock = _sellOverBlock;
        draws[currentDraw].winCombination = new uint8[](6);
        emit DrawCreated(drawsCount);

        drawsCount++;

        if(currentDraw == 1){
            jackpot = token.balanceOf(this);
        }
    }

    /**
     * @dev Finishes draw
     */
    function finishDraw() external {
        require(currentDraw == drawsCount);
        require(stopBlock < block.number, "Stop block was not reached");
        require(draws[currentDraw].winCombination[0] != 0 || block.number > (blockForRandom + 255));
        if(jackpotWinners.length > 0 && jackpot > 0){
            sendJackpots();
        }
        serviceReward(token.balanceOf(this));
        emit DrawFinished(currentDraw);
        currentDraw++;
        draws[currentDraw].winCombination = new uint8[](6);
    }

    /** 
     * @dev Sends jackpot to winners
     */
    function sendJackpots() internal {
        uint256 amount = jackpot / jackpotWinners.length;

        for(uint256 i = 0; i < jackpotWinners.length; i++) {
            token.transfer(jackpotWinners[i], amount);
        }

        delete jackpotWinners;
        jackpot = 0;
    }

    /**
     * @dev Stops draw for claiming rewards excluding jackpots 
     */
    function stopDraw() external onlyOwner {
        // require(block.number > blockForRandom, "Block number is not reached");
        draws[currentDraw].winCombination = generateWinCombination(currentDraw);
        draws[currentDraw].multiplier = generateMultiplier();
    }

    /**
     * @dev Returns stats of the draw
     * @param _drawId ID of the draw
     * @return {
        "_winnerCount": "Amount of winners in this draw",
        "_ticketCount": "Amount of purchased tickets",
        "_winCombination": Array of winning numbers
     }
     */
    function getDrawStats(uint256 _drawId) external view returns (uint _winnerCount, uint _ticketCount, uint8[] _winCombination) {
        _winnerCount = draws[_drawId].winnerCount;
        _ticketCount = draws[_drawId].ticketCount;
        _winCombination = draws[_drawId].winCombination;
    }

    /**
     * @dev Calcs reward for tickets without jackpots
     * @param _ids Ticket ids
     */
    function solveReward(uint256[] _ids) public view returns (uint256 _total) {
        for (uint j=0; j<_ids.length; j++) {
            address lottery;
            bytes8 data;
            uint256 startDraw;
            (lottery, data, startDraw) = tickets.tickets(_ids[j]);
            uint8[8] memory _data;

            for(uint q=0; q<8; q++) {
                _data[q] = uint8(data[q]);
            }

            if (_data[6] > 1)
               require(_ids.length <= 5, "you cant check more than 5 tickets with multidraw");

            require(lottery == address(this));
            for (uint l=startDraw; l < startDraw + _data[6] && l<=currentDraw && draws[l].winCombination[0] != 0; l++) {
                if (lastCheckedDrawById[_ids[j]] >= l)
                    continue;

                uint white = 0;
                uint red = 0;
                uint256 prize = 0;

                for (uint i=0; i < 5; i++)
                    if (_data[i] == draws[l].winCombination[i]) white++;
                if (_data[5] == draws[l].winCombination[5]) red++;

                if (white == 5 && red == 0) {
                    prize = multipliers[0];
                    if (_data[7] != 0) prize *= 2;
                } else if (white == 4 && red == 1) {
                    prize = multipliers[1];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 4 && red == 0) {
                    prize = multipliers[2];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 3 && red == 1) {
                    prize = multipliers[3];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 3 && red == 0) {
                    prize = multipliers[4];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 2 && red == 1) {
                    prize = multipliers[5];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 1 && red == 1) {
                    prize = multipliers[6];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 0 && red == 1) {
                    prize = multipliers[7];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                }

                _total += prize;
            }

        }
    }

    /**
     * @dev get reward for tickets without jackpots
     * @param _ids Ticket ids
     */
    function getReward(uint256[] _ids) public {
        uint256 _total = 0;
        for (uint j=0; j<_ids.length; j++) {
            address lottery;
            bytes8 data;
            uint256 startDraw;
            (lottery, data, startDraw) = tickets.tickets(_ids[j]);
            uint8[8] memory _data;

            for(uint q=0; q<8; q++) {
                _data[q] = uint8(data[q]);
            }

            if (_data[6] > 1)
               require(_ids.length <= 5, "you cant check more than 5 tickets with multidraw");

            require(tickets.ownerOf(_ids[j]) == msg.sender);
            require(lottery == address(this));
            for (uint l=startDraw; l < startDraw + _data[6] && l<=currentDraw && draws[l].winCombination[0] != 0; l++) {
                if (lastCheckedDrawById[_ids[j]] >= l)
                    continue;

                uint white = 0;
                uint red = 0;
                uint256 prize = 0;

                for (uint i=0; i < 5; i++)
                    if (_data[i] == draws[l].winCombination[i]) white++;
                if (_data[5] == draws[l].winCombination[5]) red++;

                if (white == 5 && red == 0) {
                    prize = multipliers[0];
                    if (_data[7] != 0) prize *= 2;
                } else if (white == 4 && red == 1) {
                    prize = multipliers[1];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 4 && red == 0) {
                    prize = multipliers[2];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 3 && red == 1) {
                    prize = multipliers[3];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 3 && red == 0) {
                    prize = multipliers[4];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 2 && red == 1) {
                    prize = multipliers[5];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 1 && red == 1) {
                    prize = multipliers[6];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                } else if (white == 0 && red == 1) {
                    prize = multipliers[7];
                    if (_data[7] != 0) prize *= draws[l].multiplier;
                }

                if (prize > 0) {
                    draws[l].winnerCount++;
                    lastCheckedDrawById[_ids[j]] = l;
                    _total += prize;
                }
            }
        }
        require(bank > _total || jackpot > _total, "no funds");
        token.transfer(msg.sender, _total);
        if(bank >= _total)
            bank = bank.sub(_total);
        else
            jackpot = jackpot.sub(_total);

    }

    /**
     * @dev Calcs jackpot for tickets
     * @param _ids Ticket ids
     */
    function solveJackpot(uint256[] _ids) public view returns (uint256 _numOfJackpotTickets) {
        // todo require(_ids.length <= 5, "you cant check more than 5 tickets");
        for (uint j=0; j<_ids.length; j++) {
            address lottery;
            bytes8 data;
            uint256 startDraw;
            (lottery, data, startDraw) = tickets.tickets(_ids[j]);
            uint8[7] memory _data;

            for(uint q=0; q<7; q++) {
                _data[q] = uint8(data[q]);
            }

            require(lottery == address(this));
            require(startDraw <= currentDraw && currentDraw <= startDraw + _data[6]);
            require(checkedJackpot[_ids[j]] != currentDraw);
            require(draws[currentDraw].winCombination[0] != 0);

            uint white = 0;
            uint red = 0;

            for (uint i=0; i < 5; i++)
                if (_data[i] == draws[currentDraw].winCombination[i]) white++;
            if (_data[5] == draws[currentDraw].winCombination[5]) red++;

            if (white == 5 && red == 1)
                _numOfJackpotTickets++;
        }
    }

    /**
     * @dev get jackpot for tickets 
     * @param _ids Ticket ids
     */
    function getJackpot(uint256[] _ids) public {
        // todo require(_ids.length <= 5, "you cant check more than 5 tickets");
        for (uint j=0; j<_ids.length; j++) {
            address lottery;
            bytes8 data;
            uint256 startDraw;
            (lottery, data, startDraw) = tickets.tickets(_ids[j]);
            uint8[7] memory _data;

            for(uint q=0; q<7; q++) {
                _data[q] = uint8(data[q]);
            }

            require(tickets.ownerOf(_ids[j]) == msg.sender);
            require(lottery == address(this));
            require(startDraw <= currentDraw && currentDraw <= startDraw + _data[6]);
            require(checkedJackpot[_ids[j]] != currentDraw);
            require(draws[currentDraw].winCombination[0] != 0);

            uint white = 0;
            uint red = 0;

            for (uint i=0; i < 5; i++)
                if (_data[i] == draws[currentDraw].winCombination[i]) white++;
            if (_data[5] == draws[currentDraw].winCombination[5]) red++;

            if (white == 5 && red == 1)
                jackpotWinners.push(msg.sender);

            checkedJackpot[_ids[j]] = currentDraw;

            draws[currentDraw].winnerCount++;
        }
    }

    /** 
     * @dev Generates winning combination for the draw
     * @param _drawId ID of the draw
     */
    function generateWinCombination(uint256 _drawId) internal view returns (uint8[6]) {
        require(draws[_drawId].winCombination[0] == 0, "Win combination is already set");
        require(block.number > blockForRandom && block.number < (blockForRandom + 255), "Wrong block number");

        uint8[6] memory arRnd;
        uint8 count = 0;
        uint8 seed = 0;

        while(count < 5){
            uint8 rnd = random(69, blockForRandom - count - seed - 1);

            if(contains(arRnd, rnd, 5) == 0){
                arRnd[count] = rnd;
                count ++;
            } else {
                seed ++;
            }
        }

        arRnd[5] = random(26, blockForRandom-1);
        return arRnd;
    }

    /** 
     * @dev Purchase one ticket 
     * @param data Ticket data
     */
    function buyTicket(bytes8 data) external {
        require(block.number < sellBlock, "Sell was closed");
        require(uint8(data[6]) == 1 || uint8(data[6]) == 5 || uint8(data[6]) == 10 || uint8(data[6]) == 25 || uint8(data[6]) == 52, "5 10 25 52");
        uint8 _draws = uint8(data[6]);
        bool _powerPlay = (data[7] != 0);

        uint256 total = _draws * ticketPrice;
        if (_powerPlay) total *= 2;

        require(token.transferFrom(msg.sender, address(this), total));
        tickets.createTicket(msg.sender, data, currentDraw);
        lastCheckedDrawById.push(0);
        checkedJackpot.push(0);
        draws[currentDraw].ticketCount++;
        bank = bank.add(total);
    }

    /** 
     * @dev Purchase few ticket 
     * @param _data Ticket data
     */
    function buyFewTickets(bytes8[] _data) external {
        require(_data.length <= 15, "Cannot purchase more than 15 tickets at once");
        require(block.number < sellBlock, "Sell was closed");
        uint256 total;
        for (uint i=0; i < _data.length; i++) {
            require(uint8(_data[i][6]) == 1 || uint8(_data[i][6]) == 5 || uint8(_data[i][6]) == 10 || uint8(_data[i][6]) == 25 || uint8(_data[i][6]) == 52, "5 10 25 52");
            uint256 subTotal = (uint8(_data[i][6])-currentDraw) * ticketPrice;
            if (_data[i][7] != 0) subTotal * 2;

            tickets.createTicket(msg.sender, _data[i], currentDraw);
            lastCheckedDrawById.push(0);
            checkedJackpot.push(0);

            total += subTotal;
        }

        require(token.transferFrom(msg.sender, address(this), total));
        draws[currentDraw].ticketCount += _data.length;
        bank = bank.add(total);
    }

    /**
     * @dev Generates multiplier for contract. Can be called only once
     */
    function generateMultiplier() internal view returns (uint) {
        uint16 rnd = uint16(keccak256(abi.encodePacked(block.number, msg.sender))) % 43; //  1- 10X, 2 - 5X, 3 - 4X, 13-3X, 24 - 2X

        if(rnd < 2 && jackpot < 150000000 ether){
            return 5;
        } else if (rnd < 4){
            return 4;
        } else if (rnd < 7){
            return 3;
        } else if (rnd < 20){
            return 2;
        } else {
            return 1;
        }
    }

    /** 
     * @dev Allows to set multipliers for different combinations
     * @param _multipliers Array of multipliers
    */
    function setMultipliers(uint256[8] _multipliers) public onlyOwner {
        multipliers = _multipliers;
    }

    /**
    * @notice Get random number
    * @param upper The maximum value of a random number
    * @param _blockForRandom Value for generate random number
    * @return randomNumber Random number
    */
    function random(uint8 upper, uint256 _blockForRandom) view internal returns (uint8 randomNumber) { // must be internal
        randomNumber = uint8(keccak256(abi.encodePacked(blockhash(_blockForRandom))));
        return randomNumber % upper + 1;
    }

    /**
    * @notice Searches for an item in an array by using strict equality
    * @param array Array that we sort through
    * @param value The value we are looking for in the array
    * @return uint Returns amount of inclusions of the element in the array
    */
    function contains(uint8[6] array, uint8 value, uint8 len) pure public returns(uint _amount) {
        for (uint8 i = 0; i < len; i++) {
            if (array[i] == value) {
                _amount++;
            }
        }
        return _amount;
    }

    //// TEST FUNCTIONS ////

    /** TEST FUNCTION */
    function setCombination(uint256 _drawId, uint8[6] _numbers, uint multiplier) public {
        draws[_drawId].winCombination = _numbers;
        draws[_drawId].multiplier = multiplier;
        emit CombinationSet(_drawId);
    }

    /** TEST FUNCTION */
    function createTicket(bytes8 data) external {
        tickets.createTicket(msg.sender, data, currentDraw);
        draws[currentDraw].ticketCount++;
    }
}

