pragma solidity ^0.4.24;

import "./Lottery.sol";
import "./ERC20.sol";
import "./Tickets.sol";

/**  
 * @title Lottery Factory
 */ 
contract LotteryFactory {

	/** @dev Emits when new lottery was created */
	event LotteryCreated(address _lottery);

	/** @dev Count total lottery contract */
	uint public totalLotteries = 0; 

	/** @dev Mapping where lotteries are stored */
	mapping (address => bool) lotteries;

	/** @dev Address of the ticket ERC721 token */
	address public ticket;

	/** @dev Address of the BET token */
	ERC20 public token;

	constructor(address _ticket, address _token) public {
		ticket = Tickets(_ticket);
		token = ERC20(_token);
	} 

    /**
     * @dev Creates new lottery contract
	 * @param _minDeposit Min amount of funds to create new draw in lottery
     */
	function createLottery(uint _minDeposit) external {
		//todo _minDeposit != 0
		address newLottery = new Lottery(msg.sender, ticket, token, _minDeposit);
		totalLotteries++;
		lotteries[newLottery] = true;

		emit LotteryCreated(newLottery);
	}

	/** @return Returns true if address is lottery */
	function isLottery(address _lottery) external view returns (bool) {
	    return lotteries[_lottery];
	}
}
